package com.example.Quora.controllers;import com.example.Quora.dtos.UserDto;import com.example.Quora.models.User;import com.example.Quora.repository.UserRepository;import com.example.Quora.services.UserService;import com.example.Quora.utils.JwtUtil;import jakarta.servlet.http.HttpServletResponse;import org.springframework.http.HttpHeaders;import org.springframework.http.HttpStatus;import org.springframework.http.ResponseCookie;import org.springframework.security.authentication.AuthenticationManager;import org.springframework.security.authentication.UsernamePasswordAuthenticationToken;import org.springframework.security.core.Authentication;import org.springframework.security.core.userdetails.UsernameNotFoundException;import org.springframework.web.bind.annotation.*;import java.util.Optional;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.http.ResponseEntity;import java.util.List;@RestController@RequestMapping("/api/v1/users")public class UserController {    private int expiryCookie = 24 * 7 * 3600;    private final UserService userService;    private final AuthenticationManager authenticationManager;    private  final JwtUtil jwtUtil;    public UserController(UserService userService, AuthenticationManager authenticationManager, JwtUtil jwtUtil) {        this.userService = userService;        this.authenticationManager = authenticationManager;        this.jwtUtil = jwtUtil;    }    @GetMapping    public List<User> getAllUsers() {        return userService.getAllUsers();    }    @GetMapping("/{id}")    public ResponseEntity<User> getUserById(@PathVariable Long id) {        System.out.println("req ayi"+ id);        Optional<User> user = userService.getUserById(id);        System.out.println("res"+user);        return user.map(ResponseEntity::ok).orElseGet(() -> ResponseEntity.notFound().build());    }    @PostMapping("/signup")    public User createUser(@RequestBody UserDto userDTO) {        return userService.createUser(userDTO);    }    @PostMapping("/signin")    public ResponseEntity<?> signInUser(@RequestBody  UserDto userDto, HttpServletResponse response) {        System.out.println("aaya kya"+userDto.getEmail());        System.out.println("dc"+userDto.getEmail()+userDto.getPassword());        System.out.println(new UsernamePasswordAuthenticationToken(userDto.getEmail(), userDto.getPassword()));        Authentication authentication = authenticationManager.authenticate(new UsernamePasswordAuthenticationToken(userDto.getEmail(), userDto.getPassword()));        System.out.println("chala kya"+authentication);        if (authentication.isAuthenticated()) {            String jwtToken = jwtUtil.createToken(userDto.getEmail());            ResponseCookie cookie = ResponseCookie.from("JwtToken", jwtToken)                    .httpOnly(true)                    .maxAge(expiryCookie)                    .secure(false)                    .build();            response.setHeader(HttpHeaders.SET_COOKIE, cookie.toString());            return new ResponseEntity<>(jwtToken, HttpStatus.OK);        } else {            throw new UsernameNotFoundException("not found user email");        }    }    @DeleteMapping("/{id}")    public ResponseEntity<Void> deleteUser(@PathVariable Long id) {        userService.deleteUser(id);        return ResponseEntity.noContent().build();    }    @PostMapping("/{userId}/followTag/{tagId}")    public ResponseEntity<Void> followTag(@PathVariable Long userId, @PathVariable Long tagId) {        userService.followTag(userId, tagId);        return ResponseEntity.noContent().build();    }}