package com.example.Quora.models;import jakarta.persistence.*;import lombok.*;import java.util.List;import java.util.Set;@Entity@Getter@Setter@NoArgsConstructor@AllArgsConstructor@Builderpublic class Comment {    @Id    @GeneratedValue(strategy = GenerationType.IDENTITY)    private Long id;    @Column(nullable = false)    private String content;    @ManyToOne    @JoinColumn(name = "answer_id")    private List<Answer> answer;    @ManyToOne    @JoinColumn(name = "parent_comment_id")    private Comment parentComment;    @OneToMany(mappedBy = "parentComment")    private Set<Comment> replies;    @ManyToMany    @JoinTable(            name = "comment_likes",            joinColumns = @JoinColumn(name = "comment_id"),            inverseJoinColumns = @JoinColumn(name = "user_id")    )    private Set<User> likedBy;}