package com.example.Quora.models;import jakarta.persistence.*;import lombok.*;import java.util.List;import java.util.Set;@Entity@Getter@Setter@NoArgsConstructor@AllArgsConstructor@Builderpublic class Question {    @Id    @GeneratedValue(strategy = GenerationType.IDENTITY)    private  Long id;    @Column(nullable = false)    private String title;    @Column(nullable = false)    private String content;    @ManyToOne    @JoinColumn(name = "user_id")    private User user;    @ManyToMany    @JoinTable(            name = "question_tags",            joinColumns = @JoinColumn(name = "question_id"),            inverseJoinColumns = @JoinColumn(name = "tag_id")    )    private Set<Tag> tags;}