package com.example.Quora.models;import jakarta.persistence.*;import lombok.*;import java.util.HashSet;import java.util.List;import java.util.Set;@Entity@Data@NoArgsConstructorpublic class User {    @Id    @GeneratedValue(strategy = GenerationType.IDENTITY)    private Long id;    @Column(nullable = false)    private String name;    @Column(nullable = false, unique = true)    private String email;    @Column(nullable = false, unique = true)    private String username;    @Column(nullable = false)    private String password;    @ManyToMany(cascade = {CascadeType.PERSIST, CascadeType.MERGE}, fetch = FetchType.LAZY)    @JoinTable(            name="user_tags",            joinColumns = @JoinColumn(name="user_id"),            inverseJoinColumns = @JoinColumn(name="tag_id")    )    private Set<Tag> followedTags = new HashSet<>();}