package com.example.Quora.models;import jakarta.persistence.*;import lombok.*;import java.util.List;import java.util.Set;@Entity@Getter@Setter@NoArgsConstructor@AllArgsConstructor@Builderpublic class User {    @Id    @GeneratedValue(strategy = GenerationType.IDENTITY)    private  Long id;    @Column(nullable = false)    private String name;    @Column(nullable = false, unique = true)    private String email;    @Column(nullable = false, unique = true)    private String username;    @Column(nullable = false)    private String password;    @OneToMany(mappedBy = "user")    private List<Question> questions;    @ManyToMany    @JoinTable(            name="user_tags",            joinColumns = @JoinColumn(name="user_id"),            inverseJoinColumns = @JoinColumn(name="tag_id")    )    private Set<Tag> followedTags;}